import{_ as e,o as t,c as i,a as l}from"./app.57a3dbd1.js";const b=JSON.parse('{"title":"Public steps","description":"","frontmatter":{"contributors":["ZhouYixun"]},"headers":[{"level":2,"title":"Add public steps","slug":"add-public-steps","link":"#add-public-steps","children":[]},{"level":2,"title":"Logical processing associated with steps","slug":"logical-processing-associated-with-steps","link":"#logical-processing-associated-with-steps","children":[]},{"level":2,"title":"Exception handling associated with steps","slug":"exception-handling-associated-with-steps","link":"#exception-handling-associated-with-steps","children":[]},{"level":2,"title":"View public step details","slug":"view-public-step-details","link":"#view-public-step-details","children":[]},{"level":2,"title":"Edit public steps","slug":"edit-public-steps","link":"#edit-public-steps","children":[]},{"level":2,"title":"Delete public steps","slug":"delete-public-steps","link":"#delete-public-steps","children":[]}],"relativePath":"en/doc/doc-pub.md","lastUpdated":1716537034000}'),s={name:"en/doc/doc-pub.md"},a=l('<h1 id="public-steps" tabindex="-1">Public steps <a class="header-anchor" href="#public-steps" aria-hidden="true">#</a></h1><p>Public step management for projects.</p><h2 id="add-public-steps" tabindex="-1">Add public steps <a class="header-anchor" href="#add-public-steps" aria-hidden="true">#</a></h2><ol><li>Go to the home page and click the target item.</li><li>Click the [Test Data Management] option.</li><li>Click the [Public Step] option.</li><li>Click the [Add Common Step] button.</li><li>Enter the common step name and select the platform.</li><li>Click the [Save] button to save the basic information of the public steps.</li><li>Click the [Step List] tab.</li><li>Click the [Add Step] button.</li><li>Select the type of step to add and the related information.</li><li>Click the [Submit] button to complete the step addition.</li><li>Click the &quot;plus&quot; button in the step list to add the selected step.</li><li>Click the [Save] button again.</li><li>Public steps are added!</li></ol><h2 id="logical-processing-associated-with-steps" tabindex="-1">Logical processing associated with steps <a class="header-anchor" href="#logical-processing-associated-with-steps" aria-hidden="true">#</a></h2><p>It means the logic processing associated with the test step, including the following four types of logic.</p><ul><li><p>if: When there is no exception in this step, the sub-step will be executed;</p></li><li><p>eles if: If there is an exception in the previous if conditional step, enter the logical judgment, and execute the sub-step if there is no exception;</p></li><li><p>else: If all the above conditions fail, execute the sub-step;</p></li><li><p>while: Repeat the substep if the condition is ok.</p></li></ul><h2 id="exception-handling-associated-with-steps" tabindex="-1">Exception handling associated with steps <a class="header-anchor" href="#exception-handling-associated-with-steps" aria-hidden="true">#</a></h2><p>It means the processing plan when an exception occurs in this test step, including the following three types of exceptions.</p><ul><li><p>Ignore: Ignore the exception and continue execution (exception is not thrown during logic processing);</p></li><li><p>Warning: mark the warning and get the exception screenshot and exception stack, and then continue to execute (no exception is thrown during logic processing);</p></li><li><p>Interruption: mark failures and get exception screenshots, exception stacks and test recordings, and then interrupt execution (exceptions are thrown during logic processing).</p></li></ul><h2 id="view-public-step-details" tabindex="-1">View public step details <a class="header-anchor" href="#view-public-step-details" aria-hidden="true">#</a></h2><ol><li>Go to the home page and click the target item.</li><li>Click the [Test Data Management] option.</li><li>Click the [Public Step] option.</li><li>Click the [View Step] button of the target step.</li><li>You can view the details of the public steps.</li></ol><h2 id="edit-public-steps" tabindex="-1">Edit public steps <a class="header-anchor" href="#edit-public-steps" aria-hidden="true">#</a></h2><ol><li>Go to the home page and click the target item.</li><li>Click the [Test Data Management] option.</li><li>Click the [Public Step] option.</li><li>Click the [Edit] button of the target step.</li><li>Edit the information you need to change.</li><li>Click the [Save] button.</li><li>Public steps are edited!</li></ol><h2 id="delete-public-steps" tabindex="-1">Delete public steps <a class="header-anchor" href="#delete-public-steps" aria-hidden="true">#</a></h2><ol><li>Go to the home page and click the target item.</li><li>Click the [Test Data Management] option.</li><li>Click the [Public Step] option.</li><li>Click the [Delete] button of the target step.</li><li>Click the [OK] button again.</li><li>The public steps are deleted!</li></ol>',16),n=[a];function o(c,p,d,h,r,u){return t(),i("div",null,n)}const f=e(s,[["render",o]]);export{b as __pageData,f as default};
